mapper.select:
SELECT NO, ID, PW, NAME, EMAIL, STATE, REGISTED
  FROM MEMBER
 WHERE ID = #{id}

SELECT NO, ID, PW, NAME, EMAIL, STATE, REGISTED
  FROM MEMBER
 WHERE EMAIL = #{email}

mapper.update:
UPDATE MEMBER
   SET PW = #{pw}
 WHERE EMAIL = #{email}

UPDATE MEMBER
   SET NAME = #{name}
     , EMAIL = #{email}
 WHERE NO = #{no}

UPDATE MEMBER
   SET STATE = -1
 WHERE NO = #{no}
